-- ORLANDO AREA (~7 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('greenwood-cemetery', 'Greenwood Cemetery', 'Orlando', '1603 Greenwood Street', 'FL', 28.5476, -81.3682, 'cemetery', 'Orlando''s oldest and largest cemetery, established in 1880 and containing over 70,000 graves. The cemetery includes "Baby Land" sections housing infants and children under five, many from the nearby Sunland Hospital.', 'The ghost of Orlando''s first fire chief roams the grounds. July Perry, killed in the 1920 Ocoee Massacre for registering Black voters, haunts the area where he is buried. In Baby Land, music box tunes and children''s laughter echo among the tiny graves. Ghostly hands tug at visitors'' clothing. The Wilmott Family tomb is a hotspot for apparitions. Free monthly moonlight walking tours always sell out.', 1880, 'https://orlandohaunts.com/greenwood-cemetery/'),

  ('cassadaga-hotel', 'Cassadaga Hotel', 'Cassadaga', '355 Cassadaga Road', 'FL', 28.9833, -81.2236, 'hotel', 'Built in 1927 in the heart of America''s "Psychic Capital of the World," founded by Spiritualists in 1895. The hotel sits in a community of 40 certified mediums and nearly as many certified healers.', 'Arthur, a friendly Irish ghost, haunts the second floor, leaving behind smells of alcohol, cigars, and body odor. He flickers lights, passes through guests as cold chills, and appears as a shadow at the foot of beds. Gentleman Jack and two little girls named Sarah and Katlin also reside here. The current owner saw a transparent man by the staircase her first night. The hotel offers seances and hosts a haunted house attraction near Halloween.', 1927, 'https://orlandohaunts.com/the-haunted-hotel-cassadaga/'),

  ('devils-chair', 'The Devil''s Chair', 'Cassadaga', 'Cassadaga Cemetery Road', 'FL', 28.9819, -81.2267, 'cemetery', 'A legendary brick bench in Lake Helen''s Cassadaga Cemetery, located in a town known as the "Psychic Capital of the World." The Spiritualist community was founded in 1895 by George Colby, guided by a Native American spirit named Seneca.', 'Legend says the bench belongs to the devil himself - sit in it and he will appear. Some claim you can leave an unopened beer on the chair and return to find it empty. The chair draws thrill-seekers and paranormal investigators to this small cemetery filled with Spiritualist graves. Strange lights and shadow figures appear around the bench at night. The concentrated spiritual energy of Cassadaga seems to swirl around this spot.', 1895, 'https://usghostadventures.com/haunted-stories/31-days-of-halloween/the-devils-chair-of-cassadaga-florida/'),

  ('sunland-hospital-site', 'Sunland Hospital Site', 'Orlando', '2500 North Orange Blossom Trail', 'FL', 28.5894, -81.4047, 'hospital', 'The former site of a notorious mental hospital for children that closed in 1983 after investigations revealed abuse, neglect, and horrific conditions. The main building was demolished in 1999, but the administration building and playground remain.', 'The ghosts of children who suffered and died at Sunland still play at the site. Visitors see apparitions of children, hear screams and moans, and witness swings moving on their own after dark. One explorer saw a 10-year-old boy walk through a metal door. Before demolition, police investigated ghost lights streaking through corridors. Many Sunland children are buried at Greenwood Cemetery''s Baby Land. The former hospital site is now a children''s playground.', 1961, 'https://orlandohaunts.com/sunland-hospital/'),

  ('ashleys-restaurant', 'Ashley''s Restaurant', 'Rockledge', '1609 South US Highway 1', 'FL', 28.3164, -80.7251, 'restaurant', 'Considered the most haunted restaurant in America, originally opened as Jack''s Tavern in 1933. The Tudor-style building has operated under many names including The Mad Duchess, Loose Caboose, and Gentleman Jim''s.', 'Ethel Allen, 19, was last seen at the tavern in 1934 before her mutilated, burned body was found on the Indian River bank - she was identified by a rose tattoo with a noose. Her spirit pushes visitors on the stairs and manifests in 1930s clothing in photographs. A former manager saw boots under a bathroom stall that was empty. A little girl who died in a car crash outside wanders the building. An elderly man who lived upstairs also haunts the premises.', 1933, 'https://www.clickorlando.com/features/2025/02/17/this-florida-restaurant-is-americas-most-haunted-heres-the-story-behind-it/'),

  ('lake-helen-cassadaga', 'Cassadaga Spiritualist Camp', 'Cassadaga', '1112 Stevens Street', 'FL', 28.9836, -81.2258, 'other', 'The oldest active religious community in the southeastern United States, founded in 1895 by George Colby after his spirit guide Seneca led him to this Florida wilderness. A National Register Historic District since 1991.', 'The town exists specifically to communicate with the dead. Colby Spiritualist Temple holds services where mediums deliver messages from spirits. Many historic homes on the camp grounds are haunted by former residents who never left the community they loved. Visitors regularly experience unexplained phenomena walking the shaded streets. The concentrated spiritual energy makes this one of the most paranormally active locations in America.', 1895, 'https://www.cnn.com/travel/cassadaga-florida-spiritualist-camp-mediums'),

  ('devils-millhopper', 'Devil''s Millhopper', 'Gainesville', '4732 Millhopper Road', 'FL', 29.7092, -82.3941, 'other', 'A 120-foot-deep, 500-foot-wide sinkhole that resembles a miniature rainforest. Named for the bones and fossils found at the bottom, which early settlers believed were fed to the devil through this natural funnel.', 'According to Timucuan legend, the demon Hiti kidnapped Chief Potano''s daughter Sicuri and created this sinkhole to escape with her to the underworld. Visitors hear screams from the depths, along with moaning, growling, and snarls. Some claim to have seen the devil himself at the bottom. The sinkhole was considered a gateway to the underworld by early religious settlers. Mysterious acoustics create unsettling sounds.', 1974, 'https://www.atlasobscura.com/places/the-devil-s-millhopper-gainesville-florida');

