-- ATLANTA AREA (~10 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('the-ellis-hotel', 'The Ellis Hotel', 'Atlanta', '176 Peachtree Street NW', 'GA', 33.7586, -84.3879, 'hotel', 'The site of the deadliest hotel fire in U.S. history. On December 7, 1946, 119 people perished in the Winecoff Hotel fire, including the original owner and four high school students. The tragedy led to nationwide fire safety reforms.', 'Fire alarms mysteriously go off at 2:48 a.m. - the exact time the 1946 blaze began. Guests hear heart-wrenching screams and running footsteps in empty hallways. People outside the hotel have seen faces frozen in terror at the windows. The most disturbing sightings are of women and children with expressions matching those of mothers who threw their children from windows before succumbing to flames.', 1913, 'https://atlantaghosts.com/hauntings-of-the-ellis-hotel/'),

  ('fox-theatre', 'Fox Theatre', 'Atlanta', '660 Peachtree Street NE', 'GA', 33.7725, -84.3853, 'theater', 'Originally intended as Shriners headquarters in 1928, this lavish movie palace features a mosque-like structure and ornate Moorish design. The 4,665-seat theater has been called the "Fabulous Fox" for nearly a century.', 'Roosevelt, a boiler room worker who died in 1945, slams doors and turns off lights when disturbed. A former nurse named Mary lingers in the theater''s hidden hospital room, which still contains 1920s medical furniture - a woman once gave birth here during a show. A stagehand who fell into the orchestra pit haunts the stage. Guests in period Victorian clothing are seen when the theater is empty.', 1929, 'https://atlantaghosts.com/the-haunted-fox-theatre-in-atlanta-ga/'),

  ('oakland-cemetery', 'Oakland Cemetery', 'Atlanta', '248 Oakland Avenue SE', 'GA', 33.7488, -84.3719, 'cemetery', 'Atlanta''s oldest and largest cemetery, a 48-acre park containing 70,000 graves including 6,900 Confederate soldiers, Margaret Mitchell, Bobby Jones, and 27 former mayors. Built in 1850, it remains the city''s most historic green space.', 'Visitors hear names called in roll-call fashion, with faint voices answering "heah" and "present" near the Confederate section. One visitor even heard his own name called. A Union soldier has been seen hanging from a tree - possibly one of the Great Locomotive Chase conspirators. A bleeding Confederate appears atop graves. Jasper Newton Smith''s statue reportedly comes alive at night to roam the grounds.', 1850, 'https://atlantaghosts.com/haunted-oakland-cemetery/'),

  ('rhodes-hall', 'Rhodes Hall', 'Atlanta', '1516 Peachtree Street NW', 'GA', 33.7893, -84.3833, 'mansion', 'Known as "The Castle on Peachtree," this 1904 Romanesque mansion was built from Stone Mountain granite for furniture magnate Amos Giles Rhodes. Now headquarters for the Georgia Trust for Historic Preservation.', 'Mrs. Rhodes, who died in the home in 1927, has been seen dressed in 19th-century attire peering from windows. Ghostly children laugh and play. The most terrifying presence is the Shadow Man in the basement apartment. From 1984-1992, the mansion served as a haunted house attraction, and staff say the paranormal activity never stopped. Featured on Ghost Hunters and Georgia''s Haunted Hidden Treasures.', 1904, 'https://atlantaghosts.com/hauntedrhodeshall/'),

  ('lake-lanier', 'Lake Lanier', 'Buford', 'Lake Lanier Islands Parkway', 'GA', 34.1584, -83.9966, 'other', 'A man-made reservoir completed in the 1950s, built over the historically Black town of Oscarville, which was destroyed after racial violence in 1912. The lake has claimed an estimated 700 lives since 1956.', 'The Lady of the Lake appears in a blue dress near Browns Bridge, where Delia Parker Young and Susie Roberts'' car plunged into the water in 1958. Roberts'' bones were found in 1990. Swimmers report mysterious arms reaching from below to drag them under. Divers find submerged streets, houses, and 20 cemeteries that were never removed. Some hear church bells ringing underwater despite no church ever existing there.', 1956, 'https://www.cnn.com/2020/10/31/us/lake-lanier-urban-legends-trnd'),

  ('public-house-roswell', 'The Public House', 'Roswell', '605 Atlanta Street', 'GA', 34.0239, -84.3616, 'restaurant', 'Originally a commissary for Roswell Mill constructed in 1854, this building later housed a shoe shop and funeral home. During the Civil War, it served as a Union hospital and witnessed a tragic forbidden romance.', 'Glasses move across the bar by themselves. Shadowy figures and orbs appear throughout the building. Disembodied voices are heard in empty rooms. One cook witnessed a woman''s spirit walking through the kitchen. The tragedy of the mill women, who were charged with treason and deported north during the Civil War, echoes through the building.', 1854, 'https://www.georgiahauntedhouses.com/real-haunt/public-house.html');

