-- MARIETTA AREA (~5 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('kennesaw-house', 'Kennesaw House', 'Marietta', '1 Depot Street', 'GA', 33.9527, -84.5494, 'hotel', 'One of Marietta''s oldest buildings, originally a cotton warehouse in the 1840s before becoming an inn. James Andrews and 18 disguised Union soldiers stayed here the night before the Great Locomotive Chase in 1862.', 'The building served as a hospital and morgue during the Civil War. Visitors have descended in elevators only to witness a crowded hospital room with screaming men, blood everywhere, and weary surgeons amputating limbs. A Civil War surgeon in uniform rides the elevator. One paranormal researcher claims over 700 specters swirl through the building. A woman in a light blue antebellum dress with pink flowers has appeared to children.', 1845, 'https://atlantaghosts.com/the-haunted-kennesaw-house-marietta-ga/'),

  ('kennesaw-mountain-battlefield', 'Kennesaw Mountain Battlefield', 'Kennesaw', '900 Kennesaw Mountain Drive', 'GA', 33.9831, -84.5780, 'battlefield', 'The site of a bloody Civil War battle in June 1864 where more than 4,000 men perished, mostly Union soldiers. The national battlefield park preserves the grounds where General Sherman faced Confederate General Johnston.', 'Ghostly soldiers walk through the fields. Neighbors wake to cannon volleys in the night. Hikers hear gunfire, shouted battle commands, and screams of dying men. One reenactor was passed by a Union soldier with a badly mangled arm. The metallic smell of blood and death permeates certain areas. A mounted Union cavalry officer on horseback has crossed roads and passed through fences before fading away.', 1864, 'https://visitmariettaga.com/haunted-history-the-spooky-side-of-marietta/'),

  ('kolb-farm', 'Kolb Farm', 'Marietta', '1792 Powder Springs Road', 'GA', 33.9128, -84.6074, 'mansion', 'The site of the Battle of Kolb''s Farm on June 22, 1864, which claimed 1,350 casualties. The original Valentine Kolb farmhouse still stands, along with a small family cemetery, as a reminder of the bloodshed.', 'Civil War soldiers in long coats walk through homes built on the former farm. The Tatum family in Kolb Ridge Court saw a shadowy figure with a hat and long overcoat walk down their stairs and vanish. Their angel bell rings on its own, stopping when investigated, then starting again. Cold spots appear in upstairs rooms that feel like intruding on someone''s private space. At least three families have encountered soldiers in their homes.', 1864, 'https://www.marietta.com/mariettas-haunted-history'),

  ('william-root-house', 'William Root House', 'Marietta', '80 North Marietta Parkway', 'GA', 33.9531, -84.5520, 'mansion', 'The oldest house in Marietta, built around 1845 for the town''s first druggist, William Root. This middle-class residence has been moved twice and is now preserved as a museum by the Cobb Landmarks Society.', 'Mrs. Root''s spirit has been seen peering from the bedroom windows, dressed in finest 19th-century attire. The antique rope bed in the main bedroom appears slept-in each morning despite being tightened every night before closing. One of the Root sons died young in the house. During October, the house is decorated for a Victorian funeral to commemorate Mrs. Root''s father, who died when evil spirits were believed to surround the home.', 1845, 'https://visitmariettaga.com/haunted-history-the-spooky-side-of-marietta/'),

  ('marietta-national-cemetery', 'Marietta National Cemetery', 'Marietta', '500 Washington Avenue', 'GA', 33.9449, -84.5382, 'cemetery', 'Established in 1866 to inter nearly 10,000 Union dead from Sherman''s Atlanta campaign. The 23-acre cemetery now contains over 18,000 graves. Local resident Henry Cole donated the land hoping to bury Union and Confederate soldiers together.', 'Sites of Civil War death are prone to hauntings, and this cemetery is no exception. Visitors on ghost tours encounter the spirits of soldiers who never left the battlefield. The nearby statue of Mary Meinert in James Episcopal Church Cemetery is considered one of the spookiest spots in Cobb County. The departed spirit of Civil War soldiers and mysterious residents make themselves known throughout tours.', 1866, 'https://exploregeorgia.org/things-to-do/blog/the-five-most-haunted-places-in-marietta');

