INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('carneal-house', 'The Carneal House', 'Covington', '405 East Second Street', 'KY', 39.0833, -84.5086, 'mansion',
   'The oldest house in Covington, this 1815 mansion was built by Thomas Carneal, an early Kentucky land speculator and businessman. The elegant home witnessed nearly two centuries of Northern Kentucky history.',
   'The "Grey Lady" is Covington''s most famous ghost, haunting the Carneal House where a gruesome suicide occurred. Her apparition drifts through the rooms, appearing to visitors on ghost tours. Some say she is searching for something - or someone - she lost in life.',
   1815, 'https://linknky.com/uncategorized/2017/10/30/ghost-stories-historic-spots-covington-and-newport/'),

  ('shinkle-house', 'The Shinkle House', 'Covington', 'Garrard Street', 'KY', 39.0875, -84.5083, 'mansion',
   'This elegant Victorian mansion was home to Amos Shinkle, who built the iconic Roebling Suspension Bridge connecting Covington to Cincinnati. The house later served as a bed and breakfast in the 1980s and 1990s.',
   'Sarah Shinkle''s spirit never left her bedroom. Former owners Bernie Moorman and Don Nash operated a B&B there, and Nash once left Sarah''s room after perfecting the bedding, only to return moments later to find a distinct butt impression on the bed - as if someone invisible had sat down. The ghostly indentation appeared before his eyes.',
   1860, 'https://linknky.com/uncategorized/2017/10/30/ghost-stories-historic-spots-covington-and-newport/'),

  ('booth-memorial-hospital', 'Booth Memorial Hospital (Former)', 'Covington', 'Pike Street', 'KY', 39.0839, -84.5128, 'hospital',
   'This former Salvation Army hospital served the Covington community for decades before closing. The building''s long history of births, deaths, and medical care left supernatural residue.',
   'Local police responding to calls at the abandoned hospital had terrifying encounters with ghostly nurses still making their rounds. Residents near the site continue to report encounters with spectral medical staff. The spirits seem unaware they have passed, continuing their duties from beyond the grave.',
   1913, 'https://linknky.com/uncategorized/2017/10/30/ghost-stories-historic-spots-covington-and-newport/');

