-- HARRODSBURG/DANVILLE AREA (~2 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('shaker-village-pleasant-hill', 'Shaker Village of Pleasant Hill', 'Harrodsburg', '3501 Lexington Road', 'KY', 37.8142, -84.7397, 'other',
   'The largest restored Shaker village in America encompasses 3,000 acres with 34 original buildings. The celibate religious community flourished here from 1805 until the last Shaker left in 1923.',
   'Ghostly babies are heard crying at night near the pond - according to dark legend, the Shakers drowned unwanted children there. A security guard saw a man in Shaker clothing walking the main street in the early morning hours, but when he investigated, no one was there. The Tanyard House is particularly haunted, with heavy boots walking through hallways and visitors experiencing severe physical reactions in the attic. Room 194 in the West building is notoriously haunted.',
   1805, 'https://www.kentuckyhauntedhouses.com/real-haunt/shaker-village-pleasant-hill.html'),

  ('old-fort-harrod', 'Old Fort Harrod State Park', 'Harrodsburg', '100 South College Street', 'KY', 37.7650, -84.8442, 'museum',
   'A reconstruction of the first permanent English settlement west of the Alleghenies, established by James Harrod in 1774. The fort represents the dangerous frontier life of Kentucky''s earliest pioneers.',
   'The spirits of pioneers who lived and died defending this frontier outpost are said to linger. Visitors report seeing figures in buckskin and feeling the presence of those who fought Native American raids and endured the harsh conditions of early Kentucky settlement. The replica fort seems to have absorbed the energy of those desperate times.',
   1774, 'https://www.kentuckyafterdark.com/locations/harrodsburg-ky');

