-- OTHER LOUISIANA (~9 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('shreveport-municipal-auditorium', 'Shreveport Municipal Auditorium', 'Shreveport', '705 Elvis Presley Avenue', 'LA', 32.5169, -93.7497, 'theater', 'A historic venue that hosted The Louisiana Hayride country music show, launching careers of legends including Elvis Presley, Hank Williams, and Johnny Cash. Adjacent to Oakland Cemetery, which may contribute to the paranormal activity.', 'A young girl runs through the arena, opening and closing doors - caught on visitors'' video and appearing in photographs of empty seats. A ghost called "Sarge" loves to play with women''s hair, ruffling shorter styles upward while stroking longer hair. People claim to hear Elvis himself talking and singing in the rafters, perhaps reliving his early performances. The proximity to Oakland Cemetery may explain why spirits seem drawn to this entertainment venue.', 1929, 'https://www.ktalnews.com/entertainment-news/louisiana-haunted-places-near-me/'),

  ('logan-mansion', 'Logan Mansion', 'Shreveport', '725 Austin Place', 'LA', 32.5061, -93.7544, 'mansion', 'A carefully restored Victorian mansion near downtown Shreveport, now operating as a popular Airbnb. The interior features irreplaceable fixtures made from a wood species that has since become extinct.', 'Multiple spirits inhabit the mansion: a child''s ghost appears on the stairs, believed to be Theodora Hunt who lived across the street and often played here. A man and woman haunt the downstairs, while a spectral dog roams the library. Guests report hearing footsteps, seeing shadows, and feeling watched throughout their stays. The mansion''s paranormal activity has made it a destination for ghost enthusiasts and thrill-seekers alike.', 1897, 'https://www.shreveportbossieradvocate.com/food_and_entertainment/haunted-locations-in-the-shreveport-bossier-gibsland-louisiana-spirits-paranormal-investigations-jefferson-tx/article_3c3a305c-8a59-11ef-a0e5-6f9421239aaa.html'),

  ('harris-hall-ull', 'Harris Hall', 'Lafayette', 'University of Louisiana Campus', 'LA', 30.2139, -92.0197, 'other', 'A three-story brick dormitory built in 1925 at the University of Louisiana at Lafayette, housing female students for generations. A tragic elevator accident left a permanent spiritual resident.', 'A former student killed in a horrible elevator accident haunts the building. Security guards and students hear the elevator doors opening and closing when no one is using it. A woman in a white dress glides through the hallways. Unlike many ghosts, this spirit is reportedly friendly and has been known to help people find lost items. Her presence provides comfort rather than fear to current residents.', 1925, 'https://www.explorelouisiana.com/articles/things-do-haunted-places-louisiana'),

  ('lafayette-bed-and-breakfast', 'T''Frere''s House', 'Lafayette', '1905 Verot School Road', 'LA', 30.1778, -92.0328, 'hotel', 'One of the oldest homes in Lafayette, now operating as a charming bed and breakfast. A large barrel for catching rainwater, still in place today, holds a tragic secret.', 'A young woman drowned in the rainwater barrel - whether by accident or suicide, the stories vary. Her friendly spirit makes objects move, walks around upstairs at night, and rearranges furniture. Guests hear footsteps above when no one else is upstairs. Unlike vengeful spirits, she seems content to share her former home, making her presence known in gentle, curious ways.', 1880, 'https://www.explorelouisiana.com/articles/things-do-haunted-places-louisiana'),

  ('front-street-natchitoches', 'Front Street', 'Natchitoches', 'Front Street', 'LA', 31.7600, -93.0861, 'other', 'The historic brick-paved street along Cane River Lake in Louisiana''s oldest permanent settlement, lined with shops, restaurants, and galleries in buildings dating to the 1700s and 1800s.', 'A Confederate soldier''s ghost walks this historic street. At Plantation Treasures, a little girl spirit threw items from shelves and demanded to be greeted each day or she would "throw a temper tantrum." Shop staff learned to say hello to her every morning. The centuries-old buildings retain the spirits of those who lived, worked, and died in this French colonial outpost.', 1714, 'https://www.ktalnews.com/entertainment-news/louisiana-haunted-places-near-me/'),

  ('st-maurice-plantation', 'St. Maurice Plantation', 'Natchitoches', 'LA-494', 'LA', 31.5500, -93.0500, 'plantation', 'A historic plantation near Natchitoches that was destroyed by fire in 1980. A small cemetery on the property holds the key to the ghostly activity.', 'A child''s ghost from the cemetery visits what remains of the property, making sudden noises and rushing past startled visitors. He changes the pages on calendars and is believed to have started the 1980 fire that destroyed the mansion. Before the fire, residents reported constant disruptions from the playful but troublesome spirit who refused to rest in his nearby grave.', 1820, 'https://www.theshadowlands.net/places/louisiana.htm'),

  ('chalmette-battlefield', 'Chalmette Battlefield', 'Chalmette', '8606 West St. Bernard Highway', 'LA', 29.9447, -89.9867, 'battlefield', 'The site of America''s greatest victory in the War of 1812, where General Andrew Jackson''s outnumbered forces defeated the British on January 8, 1815. Part of Jean Lafitte National Historical Park, named for the pirate who aided the American cause.', 'Apparitions of soldiers appear on the battlefield, and disembodied voices call out through the fog. The Beauregard House on the grounds has its own activity: footsteps echo through empty rooms, and shadow people move through the historic structure. The spirits of the more than 2,000 British casualties and the American defenders seem unable to leave this sacred ground. The adjacent national cemetery, holding over 16,000 American troops, adds to the spiritual energy.', 1815, 'https://www.southernspiritguide.org/chalmette-battlefield/'),

  ('saenger-theatre-new-orleans', 'Saenger Theatre', 'New Orleans', '1111 Canal Street', 'LA', 29.9533, -90.0708, 'theater', 'A magnificent 1927 atmospheric theatre designed to resemble an Italian Baroque courtyard under a starlit sky. The 4,000-seat venue took three years to build and cost $2.5 million. After Hurricane Katrina flooding, it was restored and reopened in 2013.', 'A ghost named Joe haunts the theatre, along with a cheerful woman from the late 1920s who roams the aisles leaving a unique scent in her path. Staff hear unexplained sounds, see figures in empty sections, and feel watched during late-night work. The theatre''s nearly 100-year history has accumulated spirits who seemingly cannot leave this beautiful entertainment palace.', 1927, 'https://metalinsider.net/columns/halloween-countdown/metal-insiders-halloween-2025-countdown-31-haunted-venues-day-29-30-egyptian-theatre-saenger-theatre'),

  ('marie-laveau-house', 'Marie Laveau''s House', 'New Orleans', '1022 St. Ann Street', 'LA', 29.9611, -90.0656, 'other', 'The former residence of Marie Laveau, the legendary Voodoo Queen of New Orleans who practiced her craft and held ceremonies here in the 1800s. Though the original structure is gone, the spiritual energy remains.', 'Marie Laveau''s ghost has been spotted meandering down the street past her old home, recognizable by her red and white turban and brilliantly colored clothes. She held voodoo ceremonies in the backyard, calling upon spirits that some say never left. Visitors report feeling her powerful presence, smelling incense and herbs, and sensing they are being watched. Her spirit seems to patrol the French Quarter she once ruled.', 1830, 'https://neworleansghosttour.com/marie-laveaus-house/');
