-- ANNAPOLIS AREA (~8 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('maryland-inn-annapolis', 'The Maryland Inn', 'Annapolis', '16 Church Circle', 'MD', 38.9783, -76.4919, 'hotel', 'The longest-running hotel in the United States, dating back as early as 1772. This four-story, thirty-nine room hotel sits in the heart of historic Annapolis and is said to have a ghost in every room.', 'Captain Charles Campbell and his bride haunt the inn - he was killed by a horse-drawn carriage returning from sea to reunite with her, and she took her own life minutes later. The Bride paces the fourth floor while the Captain appears in naval uniform in the basement taproom. A woman in black died on the staircase and still frequents it. Unseen children giggle and scamper down hallways. A Revolutionary soldier sings sea shanties in the basement.', 1772, 'https://www.historichotels.org/us/hotels-resorts/historic-inns-of-annapolis/ghost-stories.php'),

  ('governor-calvert-house', 'Governor Calvert House', 'Annapolis', '58 State Circle', 'MD', 38.9789, -76.4908, 'hotel', 'This 1727-era structure is part of the Historic Inns of Annapolis complex. The building has served various purposes over nearly three centuries, accumulating spirits from each era.', 'Multiple apparitions haunt the building. A man in old-fashioned clothing appears within its walls. A female spirit from a later era, given her dress style, also manifests. Most chilling is Dominic, a specter who enjoys being dead because it allows him to spy on the living more effectively. Guests report feeling watched throughout their stay.', 1727, 'https://annapolisghosttour.com/the-most-haunted-hotels-in-annapolis-md/'),

  ('reynolds-tavern', 'Reynolds Tavern', 'Annapolis', '7 Church Circle', 'MD', 38.9786, -76.4914, 'hotel', 'A three-room brick inn first constructed in 1747, Reynolds Tavern has served Annapolis visitors for over 275 years. The charming colonial building retains its 18th-century atmosphere along with a former proprietress who refuses to leave.', 'The former proprietress lingers within the tavern, watching over the establishment she once ran. Staff report objects moving on their own, cold spots in unexpected places, and the feeling that someone is supervising their work. The tavern''s cozy atmosphere is occasionally interrupted by unexplained occurrences.', 1747, 'https://annapolisghosttour.com/the-most-haunted-hotels-in-annapolis-md/');

