-- POINT LOOKOUT/SOUTHERN MD (~5 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('point-lookout-lighthouse', 'Point Lookout Lighthouse', 'Scotland', '11175 Point Lookout Road', 'MD', 38.0522, -76.3256, 'lighthouse', 'Built in 1830, Point Lookout Lighthouse has been called the most haunted lighthouse in the United States. During the Civil War, Camp Hoffman held up to 50,000 Confederate prisoners nearby, with nearly 4,000 dying from disease and exposure.', 'Pioneer paranormal researcher Hans Holzer recorded 24 different voices using EVPs. Ann Davis, wife of the first keeper, appears in a blue skirt and white blouse saying "this is my house." A gaunt Confederate prisoner in tattered clothes smelling of mildew and gunpowder shuffles across the road from the former smallpox unit. Second Mate Joseph Heaney, who drowned in the 1878 Gale, knocks at the door during storms.', 1830, 'https://exemplore.com/paranormal/GhostsofPointLookoutMarylandandtheMostHauntedLighthouseintheUSA'),

  ('point-lookout-state-park', 'Point Lookout State Park', 'Scotland', '11175 Point Lookout Road', 'MD', 38.0522, -76.3256, 'park', 'The southernmost tip of a peninsula at the confluence of the Chesapeake Bay and Potomac River. The site of Camp Hoffman, the largest Federal facility for Confederate prisoners during the Civil War, where nearly 4,000 men perished.', 'The trauma of the prison camp pervades the entire park. Visitors report seeing lines of Confederate prisoners still marching, gaunt and hollow-eyed. Screams of the dying echo across the grounds at night. The smell of death and disease occasionally wafts through the air with no source. Featured on TV shows Weird Travels, Mystery Hunter, and TLC''s Haunted Lighthouses.', 1862, 'https://dnr.maryland.gov/Pages/Spirits-of-Point-Lookout.aspx');

