-- FREDERICK AREA (~6 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('barbara-fritchie-house', 'Barbara Fritchie House', 'Frederick', '154 West Patrick Street', 'MD', 39.4143, -77.4147, 'museum', 'This home now operates as a museum dedicated to Barbara Fritchie, the heroine of John Greenleaf Whittier''s famous 1864 ballad who allegedly stood up to Confederate troops as they marched through Frederick.', 'A chair rocks by itself, and a pair of feet have been seen beneath a quilt draped over it. Lights turn on and off by themselves in the basement of the house next door. Inexplicable flickering lights are seen from outside. Barbara reportedly drops buckets of water onto passersby whose only crime is dressing in the same color as Confederates.', 1812, 'https://www.marylandhauntedhouses.com/real-haunt/barbara-fritchie-house.html'),

  ('schifferstadt', 'Schifferstadt Architectural Museum', 'Frederick', '1110 Rosemont Avenue', 'MD', 39.4253, -77.4344, 'museum', 'This German Colonial house dates to the 1750s and is one of Frederick''s oldest buildings. A testament to the town''s deep-rooted German heritage, the stone structure is celebrated for both its architectural significance and supernatural residents.', 'Original owners Joseph and Elias Brunner are believed to haunt their former home. Witnesses report unexplained voices speaking in the Brunners'' native German and footsteps throughout the house. Visitors and staff have reported strange noises, flickering lights, and the unmistakable feeling of being watched. One visitor''s daughter saw the spirits of an old couple during a school field trip.', 1756, 'https://www.marylandhauntedhouses.com/real-haunt/schifferstadt.html');

