-- EASTERN SHORE (~8 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('furnace-town', 'Furnace Town Living Heritage Village', 'Snow Hill', '3816 Old Furnace Road', 'MD', 38.1853, -75.4461, 'other', 'Near the Nassawango Iron Furnace, Furnace Town preserves the history of a 19th-century company town. When the furnace shut down, all residents were asked to leave - except one who refused.', 'Sampson Harmon, a free African American born in 1790, refused to leave when the town closed. He stayed for decades with only cats as companions, particularly a black cat named Tom. In 1896, he was forced to the Alms House where he died at 107, his dying wish to be buried at Furnace Town never granted. Stray cats now inhabit the town, and Harmon''s ghost can be heard calling them.', 1828, 'https://m.ocean-city.com/the-eastern-shores-haunted-history-and-resident-ghosts/'),

  ('poplar-hill-mansion', 'Poplar Hill Mansion', 'Salisbury', '117 Elizabeth Street', 'MD', 38.3689, -75.5947, 'mansion', 'This Federal-style mansion was built between 1795 and 1805 and is now operated as a house museum. The elegant home retains its period furnishings along with the spirit of a servant who died a tragic death.', 'Sara, a servant, still resides in the house where she worked. She died after her skirt became engulfed in flames while tending to children in an upstairs rear bedroom. Today, scorch marks are still visible on the wood floor surrounding the bedroom''s fireplace. Sara''s ghost is said to watch over the house she served in, particularly the area where she met her fiery end.', 1805, 'https://m.ocean-city.com/the-eastern-shores-haunted-history-and-resident-ghosts/'),

  ('ocean-city-life-saving-station', 'Ocean City Life-Saving Station Museum', 'Ocean City', '813 South Atlantic Avenue', 'MD', 38.3264, -75.0847, 'museum', 'The Life-Saving Station building dates to 1891 and was originally a boardinghouse for young men before becoming the museum it is today. The building''s seaside location and history of rescue operations has attracted spirits.', 'Sightings include a male youth wearing overalls on the porch, clunky footsteps throughout the building, a woman''s voice yelling, and jazz music playing inside the hotel despite no source. The spirits seem connected to the building''s history as a boardinghouse and its role in ocean rescues.', 1891, 'https://m.ocean-city.com/the-eastern-shores-haunted-history-and-resident-ghosts/'),

  ('rackliffe-house', 'Rackliffe House', 'Berlin', 'Assateague Island', 'MD', 38.2147, -75.1556, 'mansion', 'Located near Assateague Island, the Rackliffe House is home to what paranormal investigators call the "haunted trifecta" - a murder, an accidental death, and a suicide all occurred here, creating optimal conditions for hauntings.', 'The combination of violent and tragic deaths within the house has created intense paranormal activity. Visitors report seeing apparitions, hearing unexplained sounds, and feeling sudden temperature drops. The house''s isolation near the wild ponies of Assateague adds to its eerie atmosphere.', 1830, 'https://chesapeakeghosts.com/ocean-city/'),

  ('shoreham-hotel', 'Shenanigan''s/Shoreham Hotel', 'Ocean City', '4th Street & Boardwalk', 'MD', 38.3356, -75.0842, 'hotel', 'Allegedly one of the most paranormally active buildings in Ocean City, this boardwalk location has a triad of ghastly specters connected to its long history of entertaining beach visitors.', 'Three distinct ghosts haunt the building: a writer who killed himself, a man who died in the basement, and Betsey, who jumped from a third-floor window. The combination of these tragic deaths has created one of Ocean City''s most actively haunted locations. Staff and guests regularly report encounters with all three spirits.', 1920, 'https://chesapeakeghosts.com/ocean-city/');

