INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('national-road-inn', 'National Road Inn', 'Clear Spring', '12905 National Pike', 'MD', 39.6542, -77.9339, 'hotel', 'A historic tavern along the National Road, America''s first federally funded highway. The inn has served weary travelers since the early 1800s and maintains its frontier hospitality along with some permanent guests.', 'Travelers on the National Road in the 1800s sometimes never reached their destinations. The spirits of wayfarers who met unfortunate ends at or near the inn are said to still seek lodging. Guests report hearing footsteps, doors opening and closing, and the sensation of invisible company in their rooms.', 1820, 'https://www.visitmaryland.org/list/ghost-tours-haunted-attractions'),

  ('antietam-national-cemetery', 'Antietam National Cemetery', 'Sharpsburg', '5831 Dunker Church Road', 'MD', 39.4731, -77.7436, 'cemetery', 'Established in 1865 to inter Union soldiers killed at the Battle of Antietam, this cemetery contains approximately 4,776 interments, nearly 1,836 of which are unknown. The rolling hills hold the remains of those who fell on America''s bloodiest day.', 'At night, the sounds of battle seem to resume - cannon fire, musket shots, and the screams of the wounded. Visitors report seeing soldiers in blue uniforms walking among the headstones. Names are called out as if during roll call, with faint voices answering. The grief of September 17, 1862 has never fully faded from this sacred ground.', 1865, 'https://annapolisghosttour.com/antietam-national-battlefield/');
