-- NASHVILLE AREA (~12 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('ryman-auditorium', 'Ryman Auditorium', 'Nashville', '116 5th Avenue North', 'TN', 36.1612, -86.7769, 'theater', 'Built in 1892 as the Union Gospel Tabernacle, the Ryman became home to the Grand Ole Opry in 1943. The "Mother Church of Country Music" has hosted legendary performers and harbors the spirits of many who never left.', 'Thomas Ryman, the founder, haunts the venue he built for religious revivals - not honky-tonk. If performers are too risque, his ghost voices discontent. Patsy Cline, killed in a 1963 plane crash, can be heard singing "I Fall to Pieces" on the empty stage. Hank Williams, who died in 1953, is spotted backstage and in the alley. The "Grey Man" also roams the auditorium. The "Opry Curse" is blamed for 37 untimely deaths of performers.', 1892, 'https://ghostcitytours.com/nashville/haunted-nashville/ryman-auditorium-ghosts/'),

  ('hermitage-hotel', 'The Hermitage Hotel', 'Nashville', '231 6th Avenue North', 'TN', 36.1650, -86.7822, 'hotel', 'Nashville''s only Forbes Five-Star hotel, opened in 1910 in stunning Beaux-Arts style. The hotel has hosted presidents, celebrities, and political intrigue for over a century, along with guests who never checked out.', 'Numerous guests and staff have reported paranormal activity, particularly on the upper floors. Unexplained cold spots, objects moving on their own, and shadowy figures have been witnessed throughout the elegant hotel. Some believe the spirits are connected to the hotel''s role as a meeting place for suffragettes and politicians during pivotal moments in history.', 1910, 'https://nashvilleghosts.com/top-10-most-haunted-places-in-nashville/'),

  ('union-station-hotel', 'Union Station Hotel', 'Nashville', '1001 Broadway', 'TN', 36.1564, -86.7853, 'hotel', 'This stunning Romanesque Revival train station was built in 1900 and converted to a luxury hotel in 1986. The grand lobby with its 65-foot barrel-vaulted ceiling once echoed with the sounds of travelers - some of whom never departed.', 'A woman named Abigail is said to have thrown herself from the balcony after learning her soldier love died in WWI. Her spirit still wanders the lobby and upper floors. Staff report hearing footsteps in empty halls, doors slamming, and seeing a woman in early 1900s clothing near the mezzanine level. The romance of the railway era seems trapped within these walls.', 1900, 'https://nashvilleghosts.com/top-10-most-haunted-places-in-nashville/'),

  ('tennessee-state-capitol', 'Tennessee State Capitol', 'Nashville', '600 Dr. Martin L King Jr Boulevard', 'TN', 36.1658, -86.7844, 'historic_site', 'Completed in 1859 after 14 years of construction, the Tennessee State Capitol is one of the oldest working state capitols in the nation. Architect William Strickland is entombed within its walls, as is President James K. Polk.', 'William Strickland died during construction and, at his request, was entombed in the north wall of the building he designed. His ghost is said to walk the halls, particularly near his final resting place. Staff working late have reported footsteps, doors opening, and an overwhelming sense of being watched by the dedicated architect.', 1859, 'https://nashvilleghosts.com/top-10-most-haunted-places-in-nashville/'),

  ('travellers-rest-plantation', 'Travellers Rest Plantation', 'Nashville', '636 Farrell Parkway', 'TN', 36.0681, -86.7650, 'mansion', 'One of Nashville''s oldest historic homes, built in 1799 by Judge John Overton, a close friend of Andrew Jackson. The house served as Confederate headquarters during the Civil War and witnessed the bloodiest day of the Battle of Nashville.', 'Confederate soldiers who died in the Battle of Nashville are said to haunt the grounds. Visitors report seeing soldiers in gray uniforms and hearing the sounds of battle. The ghost of a woman in period dress has been seen on the upstairs porch. Cold spots and unexplained noises occur frequently throughout the property.', 1799, 'https://nashvilleghosts.com/top-10-most-haunted-places-in-nashville/');

