-- FRANKLIN AREA (~6 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('carnton-plantation', 'Carnton Plantation', 'Franklin', '1345 Eastern Flank Circle', 'TN', 35.9108, -86.8333, 'mansion', 'Built in 1826, Carnton became the principal field hospital during the Battle of Franklin on November 30, 1864. More than 1,750 Confederate soldiers died that night, and four dead generals were laid on the back porch. The McGavock Confederate Cemetery is the largest privately owned military cemetery in the U.S.', 'A woman floats across the back porch, and a Confederate soldier paces the front porch during October. A man in a general''s uniform appears on the second-story balcony. A floating head haunts the kitchen. Drums, marching, and footsteps echo through empty rooms. A young girl murdered before the war sweeps floors at dusk. The bloodstains from hospital floors remain visible today.', 1826, 'https://nashvilleghosts.com/haunted-carnton-plantation/'),

  ('carter-house', 'The Carter House', 'Franklin', '1140 Columbia Avenue', 'TN', 35.9264, -86.8689, 'mansion', 'The epicenter of the Battle of Franklin, where 23 members of the Carter family hid in the basement while fighting raged around their home. Young Tod Carter, serving for the Confederacy, was mortally wounded within sight of his home.', 'Tod Carter''s ghost is said to return to the house where he died in his family''s arms. The house has over 1,000 bullet holes from the battle. Visitors report seeing soldiers, hearing gunfire and screams, and experiencing overwhelming sadness. The Carter family''s tragedy during the bloodiest hours of the Civil War left permanent supernatural residents.', 1830, 'https://franklinis.com/franklins-haunted-history/'),

  ('lotz-house', 'The Lotz House', 'Franklin', '1111 Columbia Avenue', 'TN', 35.9267, -86.8694, 'mansion', 'Built in 1858 by German immigrant Johann Albert Lotz, this home sat directly in the path of the Confederate assault during the Battle of Franklin. The family fled to the Carter House basement as fighting engulfed their property.', 'The terror of the Lotz family seems to have left its mark on the house. Visitors report cold spots, unexplained sounds, and apparitions in period clothing. The ghosts of wounded soldiers who were brought into the house as a field hospital are believed to remain. Paranormal investigations have documented significant activity.', 1858, 'https://williamsonsource.com/3-haunted-places-in-franklin/');

