-- MEMPHIS AREA (~8 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('orpheum-theatre-memphis', 'Orpheum Theatre', 'Memphis', '203 South Main Street', 'TN', 35.1397, -90.0533, 'theater', 'The South''s Finest Theatre has had a turbulent history since opening in 1928, replacing an earlier theater destroyed by fire. A 1979 investigation by University of Memphis paranormal researchers identified seven spirits in residence.', 'A little girl named Mary, killed in a streetcar accident in the 1920s, is the most well-known ghost. She plays the famous Wurlitzer organ, plays tricks on stagehands, and always sits in seat C5 - which the theater leaves empty for her. Organist Vincent Astor first noticed doors opening on their own and lights flickering. Mary''s apparition has been photographed multiple times.', 1928, 'https://usghostadventures.com/memphis-ghost-tour/orpheum-theatre/'),

  ('ernestine-and-hazels', 'Ernestine & Hazel''s', 'Memphis', '531 South Main Street', 'TN', 35.1364, -90.0528, 'restaurant', 'Once a church, then a pharmacy, then a notorious brothel, Ernestine & Hazel''s is now a dive bar known for its Soul Burgers - and as the most haunted building in Memphis. The former brothel''s checkered past ensures plenty of supernatural company.', 'The jukebox plays on its own, selecting songs meaningful to the deceased. Ernestine''s ghost haunts the ladies'' room while Hazel is seen in the kitchen. Upstairs rooms from the brothel days harbor numerous spirits - some playful, others unsettling. Staff refuse to go upstairs alone after dark. The building''s history of sin and secrets has left permanent paranormal residents.', 1912, 'https://www.actionnews5.com/story/23957973/haunted-memphis-shares-chilling-investigations-of-haunted-places/'),

  ('woodruff-fontaine-house', 'Woodruff-Fontaine House', 'Memphis', '680 Adams Avenue', 'TN', 35.1478, -90.0422, 'mansion', 'This magnificent French Victorian mansion in Victorian Village was built in 1870 and is now a house museum. The elegant home witnessed both grandeur and tragedy during Memphis''s gilded age.', 'Mollie Woodruff, who died in the house of yellow fever, is the primary ghost. Her apparition has been seen in period dress throughout the mansion. Visitors report the strong scent of roses - her favorite flower - in rooms where no flowers are present. The chandelier in the main parlor sometimes swings on its own. EVPs have captured a woman''s voice saying "Stay."', 1870, 'https://addictedtovacation.com/haunted-houses-memphis/'),

  ('hunt-phelan-home', 'Hunt-Phelan Home', 'Memphis', '533 Beale Street', 'TN', 35.1389, -90.0456, 'mansion', 'One of the oldest remaining antebellum homes in Memphis, built in 1828. The house served as Grant''s headquarters during the Civil War and witnessed the planning of the Vicksburg Campaign.', 'Civil War soldiers from both sides are said to haunt the property where important military decisions were made. The ghost of a woman in a white dress has been seen in the gardens. Staff report doors opening and closing, footsteps on the stairs, and the sound of conversations in empty rooms. The weight of Civil War history seems permanently imprinted here.', 1828, 'https://addictedtovacation.com/haunted-houses-memphis/');

