-- CHATTANOOGA AREA (~6 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('read-house-hotel', 'The Read House Hotel', 'Chattanooga', '827 Broad Street', 'TN', 35.0461, -85.3094, 'hotel', 'Established in 1872, the Read House has endured the Civil War, cholera and yellow fever outbreaks, and over 150 years of Chattanooga history. Room 311 is so infamous that guests can specifically request a stay in the haunted chamber.', 'Annalisa Netherly was allegedly beheaded in the bathtub of Room 311 by a jealous lover in 1927. Her ghost particularly dislikes men who smoke. Guests report feelings of being watched, loud noises at all hours, unexplained shadows, flickering lights, and running water. Legend also says Al Capone stayed in Room 311 during his federal trial, with security bars installed to prevent escape.', 1872, 'https://ghostcitytours.com/chattanooga/haunted-chattanooga/read-house-hotel/'),

  ('chickamauga-battlefield', 'Chickamauga & Chattanooga National Military Park', 'Fort Oglethorpe', '3370 LaFayette Road', 'TN', 34.9393, -85.2600, 'battlefield', 'The site of the second bloodiest battle of the Civil War after Gettysburg. On September 19-20, 1863, over 34,000 casualties occurred. The Cherokee word "Chickamauga" means "River of Death."', 'Old Green Eyes is the battlefield''s most famous ghost - a figure with glowing green eyes, possibly a soldier whose head was blown off by a cannonball searching for his body. Some describe him as a gentleman in a top hat with stringy hair. In 1976, a ranger encountered a tall man in a black duster who vanished. Drivers have wrecked after seeing unearthly green eyes at Snodgrass Hill.', 1863, 'https://choosechatt.com/blog/chattanoogas-haunted-history/'),

  ('stone-bridge-at-chickamauga', 'Stone Bridge at Chickamauga', 'Fort Oglethorpe', 'Brotherton Road', 'TN', 34.9350, -85.2556, 'battlefield', 'A key crossing point during the Battle of Chickamauga, this stone bridge witnessed some of the heaviest fighting of the September 1863 battle. The structure still stands as a silent witness to the carnage.', 'Soldiers from both sides are seen crossing the bridge at night, some wounded and stumbling, others marching in formation. The sounds of battle - gunfire, cannons, screaming horses - echo across the fields. Visitors report sudden cold spots on the bridge and the feeling of hands pushing them. The intensity of two days of fighting left permanent spiritual wounds on the landscape.', 1863, 'https://choosechatt.com/blog/chattanoogas-haunted-history/');

