-- MURFREESBORO/MIDDLE TN (~6 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('stones-river-national-battlefield', 'Stones River National Battlefield', 'Murfreesboro', '3501 Old Nashville Highway', 'TN', 35.8772, -86.4319, 'battlefield', 'The Battle of Stones River from December 31, 1862 to January 2, 1863 had the highest percentage of casualties of any major Civil War battle. The Slaughter Pen and Stones River National Cemetery hold thousands of fallen soldiers.', 'A headless horseman - believed to be Lt. Col. Julius Peter Garesch√©, decapitated by a cannonball while on horseback - is seen riding across the battlefield. A "ghost hunter" claims thousands of spirits inhabit the park. Re-enactors report soldiers who vanish when approached. The smell of gunpowder and sounds of battle persist at tour stops four and six.', 1862, 'https://www.wkrn.com/special-reports/haunted-tennessee/soldiers-and-spirits-haunt-stones-river-battlefield-in-murfreesboro/'),

  ('oaklands-mansion', 'Oaklands Mansion', 'Murfreesboro', '900 North Maney Avenue', 'TN', 35.8619, -86.3844, 'mansion', 'This elegant antebellum home witnessed the surrender of Murfreesboro to Confederate forces in 1862. Confederate President Jefferson Davis visited, and the house changed hands between armies throughout the war.', 'The spirits of Civil War soldiers and former residents are said to haunt Oaklands. Visitors report seeing women in period dresses and soldiers in uniform. Unexplained sounds, cold spots, and the feeling of being watched are common. The trauma of occupation and divided loyalties during the war seems to have left permanent supernatural imprints.', 1815, 'https://southernmarylandchronicle.com/2018/10/11/scariest-stories-of-the-chesapeake-bay-region-part-6-point-lookout-lighthouse/'),

  ('sam-davis-home', 'Sam Davis Home', 'Smyrna', '1399 Sam Davis Road', 'TN', 35.9500, -86.5103, 'mansion', 'The boyhood home of Sam Davis, the "Boy Hero of the Confederacy," who was executed as a Confederate spy at age 21 after refusing to reveal the name of his informant. His last words: "If I had a thousand lives, I would give them all rather than betray a friend."', 'Sam Davis''s ghost is said to return to his family home. Visitors report seeing a young man in Confederate gray on the grounds and inside the house. The smell of pipe tobacco sometimes fills rooms. Staff have heard footsteps and voices when the house is empty. Sam''s loyalty unto death seems to keep his spirit tied to the land of his youth.', 1820, 'https://blueridgemountainstravelguide.com/most-haunted-places-in-tennessee/');

