-- EAST TENNESSEE (~4 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('rotherwood-mansion', 'Rotherwood Mansion', 'Kingsport', '1501 Rotherwood Drive', 'TN', 36.5300, -82.5200, 'mansion', 'Built in 1818, Rotherwood Mansion overlooks the Holston River and is considered one of the most haunted locations in East Tennessee. The home witnessed multiple tragedies that left their supernatural mark.', 'Rowena Ross, a young woman who drowned herself in the Holston River after losing two husbands, haunts the property as a "Hound of Hell" - a black dog seen roaming the grounds at night. Her ghost also appears in windows and on the grounds. The house has known so much tragedy that multiple spirits are believed to coexist within its walls.', 1818, 'https://blueridgemountainstravelguide.com/most-haunted-places-in-tennessee/'),

  ('exchange-place', 'Exchange Place', 'Kingsport', '4812 Orebank Road', 'TN', 36.5203, -82.4761, 'mansion', 'A living history farm that preserves the heritage of the Overmountain settlers from the early 1800s. The property includes original log buildings and demonstrates frontier life in East Tennessee.', 'Staff and visitors have reported seeing apparitions of settlers in period clothing going about their daily tasks - unaware they have been dead for centuries. Children''s laughter is heard when no children are present. The spirits seem benevolent, simply continuing their frontier routines in the afterlife.', 1820, 'https://easttnfamilyfun.com/haunted-houses-in-knoxville-the-smoky-mountains-and-east-tennessee/'),

  ('andrew-johnson-birthplace', 'Andrew Johnson Birthplace', 'Greeneville', '101 North College Street', 'TN', 36.1628, -82.8306, 'museum', 'The tiny building where the 17th President of the United States was born in 1808. Andrew Johnson rose from humble beginnings to become president following Lincoln''s assassination.', 'The ghost of Andrew Johnson is said to visit his birthplace, watching over the humble building where his life began. Staff have reported feeling a presence, particularly during quiet hours. Some believe Johnson returns to remember his modest origins before the weight of the presidency and impeachment trial that defined his legacy.', 1808, 'https://www.tnvacation.com/articles/meet-tennessees-ghosts-fall');
