-- HOUSTON (~5 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('julia-ideson-building', 'Julia Ideson Building', 'Houston', '550 McKinney Street', 'TX', 29.7589, -95.3689, 'other', 'This beautiful Spanish Renaissance-style building opened in 1926 as the main branch of the Houston Public Library, named after the city''s first librarian. Originally planned as part of a five-building complex, the Great Depression halted construction. The building is located near Houston City Hall and Sam Houston Park.', 'Jacob Frank Cramer was the library caretaker in the early 1900s, spending his leisure time strolling through the halls playing violin and walking with his dog Petey. In 1936, he died alone in his basement room. Ever since, employees report eerie strains of violin music, especially on gloomy days, and the clicking of Petey''s toenails on the marble floors. Both the ghost of Cramer and his faithful dog still roam the library they loved. Footsteps are heard when no one is present, and the melancholy sound of the violin floats through the stacks.', 1926, 'https://www.hauntedrooms.com/texas/houston/haunted-places'),

  ('battleship-texas', 'Battleship Texas', 'Galveston', 'Pier 21', 'TX', 29.2897, -94.7917, 'museum', 'The only surviving naval ship to have served in both World Wars, USS Texas was commissioned in 1914 and saw action in both the Atlantic and Pacific theaters. The vessel played a major role in D-Day, shelling Omaha Beach and Point du Hoc. After decades at La Porte, the ship moved to Galveston in 2022 for restoration. The Battleship Texas has been featured on Syfy''s Ghost Hunters.', 'Helmsman Chris Christiansen was killed on the bridge by enemy fire off the coast of Cherbourg on June 25, 1944 - the only man to die on the Battleship Texas. Forever a faithful sailor, he remains at his post ready for the next fight. The friendly ghost known as "Charlie" gives tours in the engine room, wearing a vintage white Navy uniform no longer in service. Visitors often compliment staff about this knowledgeable tour guide, but there is no employee by that name. Strange sounds emanate from the engine room, floating vapor appears in halls, and a young redheaded soldier has been seen on the second deck, usually smiling at visitors.', 1914, 'https://hauntedus.com/texas/uss-lexington/'),

  ('spaghetti-warehouse-houston', 'Spaghetti Warehouse', 'Houston', '901 Commerce Street', 'TX', 29.7625, -95.3594, 'restaurant', 'Housed in a historic warehouse building in downtown Houston, this restaurant became famous not just for its Italian-American cuisine but for its resident ghosts. The building''s history predates the restaurant, and the spirits seem to have stayed when the eatery moved in.', 'Staff and diners report seeing apparitions throughout the restaurant, including a woman in period dress and the ghost of a pharmacist who once worked in the building. Objects move on their own, dishes fly off tables, and cold spots appear without explanation. The most famous ghost is a little girl who plays in the dining areas, sometimes interacting with children who can see her. Staff have grown accustomed to unexplained phenomena and consider the ghosts part of the restaurant''s character.', 1972, 'https://www.chron.com/news/houston-texas/houston/article/The-haunting-of-Spaghetti-Warehouse-Reality-or-9987968.php');

