-- COLONIAL WILLIAMSBURG AREA (~8 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('peyton-randolph-house', 'Peyton Randolph House', 'Williamsburg', '100 W Nicholson Street', 'VA', 37.2710, -76.7047, 'mansion', 'One of Williamsburg''s oldest and most architecturally significant homes, built in stages beginning in 1715. Home of Peyton Randolph, considered by some to be the first President of the United States as President of the Continental Congress.', 'Known as the most haunted house on the East Coast, with as many as thirty deaths on the property. General Lafayette reported a phantasmic hand kept him from entering. Visitors hear whispers, shouting, and footsteps. Objects move on their own, and some are pushed down stairs. A young boy''s ghost appears near the tree he fell from. The house may have been cursed by an enslaved person in 1782.', 1715, 'https://ghostcitytours.com/williamsburg/haunted-williamsburg/haunted-peyton-randolph-house/'),

  ('colonial-williamsburg-governors-palace', 'Governor''s Palace', 'Williamsburg', '306 Palace Green Street', 'VA', 37.2730, -76.7010, 'mansion', 'The official residence of royal governors of the Colony of Virginia from 1722 until the Revolution. The original building burned in 1781 while serving as a hospital for American soldiers wounded at Yorktown.', 'Soldiers who died in the makeshift hospital still roam the halls. Visitors report seeing men in Colonial-era military uniforms, hearing moans of the wounded, and smelling gunpowder. The ghost of Lady Dunmore, wife of Virginia''s last royal governor, has been seen in the gardens mourning her daughter who died here.', 1722, 'https://colonialghosts.com/'),

  ('bruton-parish-church', 'Bruton Parish Church', 'Williamsburg', '331 Duke of Gloucester Street', 'VA', 37.2709, -76.7021, 'other', 'One of the oldest Episcopal churches in America, in continuous use since 1715. Many of Virginia''s most prominent colonial figures worshipped here, including George Washington, Thomas Jefferson, and Patrick Henry.', 'Parishioners and visitors have reported seeing ghostly figures in Colonial dress sitting in the pews during services. The churchyard cemetery, with graves dating to the 1600s, is said to be visited by spirits who wander among the headstones at night. Cold spots and unexplained sounds are common inside the church.', 1715, 'https://colonialghosts.com/'),

  ('public-hospital-williamsburg', 'Public Hospital of 1773', 'Williamsburg', '326 W Francis Street', 'VA', 37.2695, -76.7072, 'hospital', 'The first public institution in British North America devoted solely to the care and treatment of the mentally ill. The original building was destroyed by fire in 1885.', 'The reconstructed building and grounds are said to be haunted by former patients. Visitors report hearing screams, crying, and the rattling of chains. Apparitions of patients in period clothing have been seen wandering the halls, still trapped in their tormented states from centuries past.', 1773, 'https://colonialghosts.com/'),

  ('george-wythe-house', 'George Wythe House', 'Williamsburg', '101 Palace Green Street', 'VA', 37.2722, -76.7018, 'mansion', 'Home of George Wythe, signer of the Declaration of Independence and America''s first law professor. George Washington used the house as headquarters before the siege of Yorktown.', 'The ghost of Lady Ann Skipwith is the most famous specter. She died at the house in 1779 and is seen in a white dress, sometimes dancing alone in the ballroom. Staff have reported objects moving, doors opening and closing, and the distinct smell of her rose perfume.', 1755, 'https://colonialghosts.com/'),

  ('william-and-mary-college', 'College of William & Mary', 'Williamsburg', '200 Stadium Drive', 'VA', 37.2707, -76.7075, 'university', 'The second-oldest institution of higher learning in America, founded in 1693. The Wren Building is the oldest academic building still in continuous use in the United States.', 'The ghost of a French soldier from the Revolutionary War haunts the Wren Building, which served as a hospital. Students report seeing him in the hallways at night. In Tucker Hall, a female ghost has been seen, believed to be a woman who died during the Civil War when the building was used as a Confederate hospital.', 1693, 'https://colonialghosts.com/'),

  ('kings-arms-tavern', 'King''s Arms Tavern', 'Williamsburg', '416 E Duke of Gloucester Street', 'VA', 37.2711, -76.6985, 'restaurant', 'A reconstructed 18th-century tavern on the site of the original, which was one of Williamsburg''s most genteel establishments. Frequented by George Washington and Thomas Jefferson.', 'Staff and diners have reported seeing a woman in Colonial dress who appears and disappears in the upstairs rooms. Glasses have been known to slide across tables, and the sound of a harpsichord plays when none is present. The spirit is believed to be a former tavern keeper''s wife.', 1772, 'https://colonialghosts.com/'),

  ('shields-tavern', 'Shields Tavern', 'Williamsburg', '422 E Duke of Gloucester Street', 'VA', 37.2711, -76.6981, 'restaurant', 'A reconstructed Colonial tavern that was one of the most popular in 18th-century Williamsburg. James Shields operated it from 1745 until his death.', 'Employees have reported hearing footsteps in empty rooms and seeing shadowy figures after closing. The ghost of James Shields is said to still check on his establishment, with patrons occasionally feeling a presence looking over their shoulder. Cold spots appear suddenly in the dining rooms.', 1745, 'https://colonialghosts.com/');

