-- RICHMOND AREA (~10 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('hollywood-cemetery', 'Hollywood Cemetery', 'Richmond', '412 S Cherry Street', 'VA', 37.5355, -77.4600, 'cemetery', 'Richmond''s most famous cemetery, final resting place of two U.S. Presidents (James Monroe and John Tyler), Confederate President Jefferson Davis, and 18,000 Confederate soldiers. A 90-foot granite pyramid memorial honors the Confederate dead.', 'The Richmond Vampire legend centers on W.W. Pool''s mausoleum. After the 1925 Church Hill Tunnel collapse, witnesses described a bloody, fanged figure fleeing to this tomb. A ghostly black dog guards a child''s grave, barking at visitors who come too close. Apparitions of Confederate soldiers march through the grounds at night.', 1847, 'https://rvaghosts.com/ghosts-of-hollywood-cemetery/'),

  ('edgar-allan-poe-museum', 'Edgar Allan Poe Museum', 'Richmond', '1914 E Main Street', 'VA', 37.5302, -77.4194, 'museum', 'Housed in the oldest building in Richmond (built 1754), the museum contains the largest collection of Poe memorabilia in the world. Richmond was Poe''s childhood home.', 'Three ghosts haunt the museum: two blonde children believed to be the original owner''s kids, and a dark shadowy figure thought to be Poe himself. Staff have heard children laughing and running up stairs. Poe''s ghost is seen reclining on his favorite sofa after visitors leave. The master of horror may have never left his beloved city.', 1754, 'https://www.hauntedrooms.com/virginia/richmond/haunted-places'),

  ('byrd-theatre', 'Byrd Theatre', 'Richmond', '2908 W Cary Street', 'VA', 37.5524, -77.4847, 'theater', 'A stunning 1928 movie palace in the Carytown district, featuring a Wurlitzer organ and ornate decorations. One of the few remaining grand theaters from the golden age of cinema.', 'Two ghosts call the Byrd home. A little girl is frequently seen playing in the women''s restroom. Former manager Robert Coulter, who worked there from 1928 to 1971, still watches over his beloved theater from the balcony. Staff hear phantom organ music when no one is at the keyboard.', 1928, 'https://rvaghosts.com/top-ten-haunted-spots-in-richmond/'),

  ('church-hill-tunnel', 'Church Hill Tunnel', 'Richmond', 'E Marshall Street', 'VA', 37.5270, -77.4110, 'other', 'A collapsed railway tunnel built in the 1870s for the Chesapeake and Ohio Railway. A 1925 cave-in killed several workers and trapped an entire train, which remains buried underground to this day.', 'The tunnel was sealed with workers and a locomotive still inside. Witnesses report hearing phantom train whistles, the grinding of wheels, and anguished screams from beneath the earth. Some claim to see ghostly figures emerging from the sealed entrance. This tragedy also spawned the Richmond Vampire legend.', 1873, 'https://rvaghosts.com/top-ten-haunted-spots-in-richmond/'),

  ('byrd-park-pump-house', 'Byrd Park Pump House', 'Richmond', '1708 Pump House Drive', 'VA', 37.5545, -77.4755, 'other', 'A Victorian-era pump house built in 1883 that once supplied water to Richmond. The Romanesque Revival building was also used as a social hall for city events.', 'Called one of the most haunted places in America, the abandoned pump house is home to numerous spirits. A young girl who drowned nearby is seen in white. Workers who died during construction appear in the windows. Visitors report being touched, pushed, and hearing their names whispered.', 1883, 'https://rvaghosts.com/top-ten-haunted-spots-in-richmond/'),

  ('agecroft-hall', 'Agecroft Hall', 'Richmond', '4305 Sulgrave Road', 'VA', 37.5759, -77.5082, 'mansion', 'A Tudor manor house originally built in Lancashire, England in the 15th century, then dismantled and shipped to Richmond in 1925 where it was reconstructed as a museum.', 'The ghosts may have traveled with the house from England. Staff and visitors report seeing a woman in Elizabethan dress wandering the gardens. Unexplained footsteps echo through the empty halls, and doors open and close on their own. Some believe the spirits are former English residents who couldn''t leave their beloved home.', 1926, 'https://www.hauntedrooms.com/virginia/richmond/haunted-places'),

  ('maymont-mansion', 'Maymont Mansion', 'Richmond', '1700 Hampton Street', 'VA', 37.5383, -77.4757, 'mansion', 'A Gilded Age estate completed in 1893 for railroad magnate James Dooley and his wife Sallie. The 100-acre property includes a mansion, gardens, and wildlife exhibits.', 'Sallie Dooley, who lived in the mansion until her death in 1925, never left. Staff have seen her ghost in the upstairs rooms, particularly near her former bedroom. The smell of her perfume wafts through the halls, and visitors feel a maternal presence watching over the home she loved.', 1893, 'https://www.hauntedrooms.com/virginia/richmond/haunted-places'),

  ('virginia-state-capitol', 'Virginia State Capitol', 'Richmond', '1000 Bank Street', 'VA', 37.5388, -77.4335, 'other', 'Designed by Thomas Jefferson and completed in 1788, it is the second-oldest working capitol building in the United States. The building witnessed the trial of Aaron Burr and served as the Confederate capitol.', 'The ghost of a Confederate soldier has been seen standing guard near the old House chamber. During the Civil War, the building served as the Confederate capitol, and some believe Jefferson Davis himself haunts the halls. Strange sounds and cold spots are reported throughout the building.', 1788, 'https://rvaghosts.com/'),

  ('linden-row-inn', 'Linden Row Inn', 'Richmond', '100 E Franklin Street', 'VA', 37.5415, -77.4356, 'hotel', 'A row of Greek Revival townhouses built in the 1840s, now converted to a boutique hotel. Edgar Allan Poe courted his childhood sweetheart Elmira in the enchanted garden here.', 'Poe''s presence lingers in the garden where he spent time with Elmira. Guests report seeing a dark figure in a cape wandering the grounds at night. Children''s laughter echoes through the halls, believed to be the ghosts of children who once lived in the townhouses.', 1847, 'https://www.hauntedrooms.com/virginia/richmond/haunted-places'),

  ('old-city-hall-richmond', 'Old City Hall', 'Richmond', '1001 E Broad Street', 'VA', 37.5407, -77.4325, 'other', 'A stunning High Victorian Gothic building completed in 1894, featuring an impressive central courtyard. The building served as Richmond''s city hall until 1971.', 'Prisoners were once held in the basement cells, and some never left. Security guards report hearing chains rattling and desperate pleas from the empty basement. A woman in Victorian dress has been seen on the upper floors, believed to be an employee who died in the building.', 1894, 'https://rvaghosts.com/');

