-- ALEXANDRIA/NORTHERN VIRGINIA (~6 locations)
-- ==========================================

INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('gadsbys-tavern', 'Gadsby''s Tavern', 'Alexandria', '134 N Royal Street', 'VA', 38.8054, -77.0429, 'restaurant', 'A complex of historic buildings including a 1785 tavern and 1792 City Hotel. George Washington, Thomas Jefferson, John Adams, and James Madison all dined here.', 'The Female Stranger is Alexandria''s most famous ghost. In 1816, a mysterious ill woman arrived with her husband. She died in Room 8, her identity kept secret to this day. Her grave in St. Paul''s Cemetery is marked only "Female Stranger." She haunts Room 8, appearing with a candle, peering from windows, and standing near her tomb.', 1785, 'https://usghostadventures.com/haunted-places/gadsbys-tavern/'),

  ('carlyle-house', 'Carlyle House', 'Alexandria', '121 N Fairfax Street', 'VA', 38.8047, -77.0422, 'mansion', 'A Georgian Palladian manor built in 1753 by Scottish merchant John Carlyle. British General Braddock planned the French and Indian War campaign here in 1755.', 'During the day, the house appears elegant, but at night the walls whisper of darker times. Soldiers died here, and the jealousy of Carlyle''s second wife burned bright. Visitors report feeling intense anguish, seeing shadowy figures, and hearing whispered arguments from long-dead residents.', 1753, 'https://alexandriaghosts.com/the-top-3-haunted-locations-in-alexandria/'),

  ('lee-fendall-house', 'Lee-Fendall House', 'Alexandria', '614 Oronoco Street', 'VA', 38.8088, -77.0451, 'mansion', 'Built in 1785 by Philip Richard Fendall, who married three different women from the Lee family. The house was home to 37 members of the Lee family and later labor leader John L. Lewis.', 'Multiple spirits inhabit this house. The ghost of a Civil War soldier has been seen on the grounds. Inside, visitors report cold spots, objects moving, and the feeling of being watched. A female presence is felt in the upstairs bedrooms, possibly one of the many Lee women who called this home.', 1785, 'https://alexandriaghosts.com/'),

  ('ramsey-house', 'Ramsay House', 'Alexandria', '221 King Street', 'VA', 38.8049, -77.0427, 'mansion', 'The oldest house in Alexandria, built in 1724 for William Ramsay, one of the city''s founders and a close friend of George Washington. Now serves as the visitor center.', 'William Ramsay and his wife Anne are said to still occupy their home. Staff have heard conversations in empty rooms and footsteps on the upper floors. The smell of tobacco smoke, Ramsay''s favorite vice, occasionally wafts through the building with no source.', 1724, 'https://alexandriaghosts.com/'),

  ('swopes-townhouse', 'Swope''s Townhouse', 'Alexandria', '210 Prince Street', 'VA', 38.8032, -77.0424, 'mansion', 'An 18th-century townhouse that was once home to Colonel Michael Swope, a Revolutionary War officer who survived capture and imprisonment by the British.', 'Colonel Swope''s spirit has never found peace. Visitors smell phantom tobacco smoke, feel sudden chills, and hear a haunting lullaby played on a piano that doesn''t exist. Swope spent his final years reliving his wartime trauma, and that anguish permeates the building.', 1780, 'https://alexandriaghosts.com/'),

  ('alexandria-lyceum', 'The Lyceum', 'Alexandria', '201 S Washington Street', 'VA', 38.8019, -77.0430, 'museum', 'A Greek Revival building constructed in 1839 as a cultural center. During the Civil War, it served as a hospital for Union soldiers. Now Alexandria''s history museum.', 'Wounded soldiers who died here never left. Staff report hearing moans and cries, especially at night. The apparition of a Union soldier has been seen standing at the windows, perhaps still waiting for news of the war''s end. Cold spots appear throughout the building.', 1839, 'https://alexandriaghosts.com/');

