INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('kenmore-plantation', 'Kenmore Plantation', 'Fredericksburg', '1201 Washington Avenue', 'VA', 38.3050, -77.4710, 'mansion', 'A Georgian mansion built in 1775 for Fielding Lewis and his wife Betty, sister of George Washington. The house is known for its ornate plasterwork ceilings and Revolutionary War history.', 'Colonel Fielding Lewis bankrupted himself manufacturing guns for the Revolution and died of tuberculosis in 1781. His ghost, dressed in period clothing, sits at an upstairs desk studying papers, still worrying about money. Heavy footsteps pace the downstairs hall, doorknobs turn by themselves, and fireplace tools are found in disarray.', 1775, 'https://colonialghosts.com/top-25-most-haunted-places-in-virginia/'),

  ('chatham-manor', 'Chatham Manor', 'Fredericksburg', '120 Chatham Lane', 'VA', 38.3033, -77.4566, 'mansion', 'A Georgian mansion overlooking the Rappahannock River, built in 1771. President Lincoln visited twice during the Civil War when it served as a Union headquarters and hospital.', 'The Lady in White appears every seven years on June 21st, walking the path from the manor to the river on "Ghost Walk." She is believed to be a young woman who died of a broken heart. Abraham Lincoln''s ghost has also been reported, standing at the windows where he once watched the Battle of Fredericksburg.', 1771, 'https://www.virginiahauntedhouses.com/real-haunt/chatham-manor.html'),

  ('fredericksburg-battlefield', 'Fredericksburg Battlefield', 'Fredericksburg', '1013 Lafayette Boulevard', 'VA', 38.2944, -77.4708, 'battlefield', 'Site of four major Civil War battles that claimed over 100,000 casualties. The battlefield includes the infamous "Bloody Angle" and Sunken Road where thousands died.', 'Called the bloodiest ground in North America. At the Bloody Angle, psychics get overwhelming readings and ghost hunters collect EVPs of battle sounds. Apparitions of soldiers appear in the mist, the smell of gunpowder fills the air, and the screams of the dying echo across the fields at night.', 1862, 'https://theweeklyringer.com/2016/10/27/fredericksburg-civil-war-urban-legends-and-ghost-stories/'),

  ('st-georges-church-fredericksburg', 'St. George''s Episcopal Church', 'Fredericksburg', '905 Princess Anne Street', 'VA', 38.3028, -77.4614, 'other', 'One of the oldest Episcopal congregations in America, established in 1732. The current building dates from 1849 and contains a Tiffany window and a cannonball lodged in a pillar from the Civil War.', 'Ghost hunters come looking for a mysterious red room that appears and then vanishes. Doors slam violently, pews are overturned overnight, and a woman in white drifts through the sanctuary. The church''s long history of funerals and Civil War trauma may explain the intense activity.', 1849, 'https://gofredericksburg.com/ghosts-and-ghouls-fredericksburgs-spookiest-spots/'),

  ('rising-sun-tavern', 'Rising Sun Tavern', 'Fredericksburg', '1304 Caroline Street', 'VA', 38.3036, -77.4604, 'restaurant', 'Built around 1760 as a residence for Charles Washington, George Washington''s youngest brother. It later became a tavern and stagecoach stop on the route between Richmond and Alexandria.', 'The spirit of a former tavern keeper makes his presence known by moving objects and creating cold spots. Visitors have seen a man in Colonial clothing in the upstairs rooms who vanishes when approached. The smell of ale and tobacco occasionally permeates the air.', 1760, 'https://usghostadventures.com/fredericksburg-ghost-tour/'),

  ('mary-washington-house', 'Mary Washington House', 'Fredericksburg', '1200 Charles Street', 'VA', 38.3027, -77.4607, 'mansion', 'The home where George Washington''s mother Mary spent her final years, purchased for her by George in 1772. She lived here until her death in 1789.', 'Mary Washington''s ghost has been seen tending her beloved garden, just as she did in life. Visitors report seeing an elderly woman in period dress among the flowers who disappears when they look directly at her. Inside, her rocking chair sometimes moves on its own.', 1772, 'https://usghostadventures.com/fredericksburg-ghost-tour/');

