INSERT OR REPLACE INTO places (slug, name, city, address, state, latitude, longitude, category, description, ghost_story, year_established, source_url)
VALUES
  ('fort-monroe', 'Fort Monroe', 'Hampton', '41 Bernard Road', 'VA', 37.0030, -76.3080, 'battlefield', 'The largest stone fort ever built in the United States, constructed between 1819-1834. It imprisoned Jefferson Davis after the Civil War and was the site where enslaved people first landed in 1619.', 'With 400 years of history, Fort Monroe teems with spirits. Edgar Allan Poe''s ghost appears in the Casemate Museum—a boy living above it identified Poe as "the man in my closet." Lincoln''s ghost sits in a rocking chair, and Women in White roam the ramparts. Ghost Alley on Matthews Lane has the heaviest concentration of activity.', 1834, 'https://www.wavy.com/living-local/haunted-hampton-roads-fort-monroe/'),

  ('moses-myers-house', 'Moses Myers House', 'Norfolk', '323 E Freemason Street', 'VA', 36.8527, -76.2873, 'mansion', 'A Federal-style townhouse built in 1792 for Moses Myers, one of America''s first millionaires. The house contains 70% of its original furnishings, making it one of the most intact historic homes in America.', 'A figure in a top hat and cloak haunts the house—believed to be Moses Myers'' business partner who was killed on the property. Staff have seen him in doorways and corridors, always wearing the same distinctive outfit. Footsteps echo through empty rooms.', 1792, 'https://coastalvirginiamag.com/article/coastal-virginia-haunted-history/'),

  ('chamberlin-hotel', 'The Chamberlin', 'Hampton', '2 Fenwick Road', 'VA', 37.0025, -76.3056, 'hotel', 'A grand waterfront hotel built in 1928 on the grounds of Fort Monroe. The original Hygeia Hotel on this site was a favorite resort of the wealthy and powerful.', 'Edgar Allan Poe gave poetry readings on the veranda, and his ghost still appears there. Guests report seeing soldiers in Civil War uniforms wandering the halls. The hotel''s military history and countless guests who passed through have left an indelible spiritual imprint.', 1928, 'https://coastalvirginiamag.com/article/coastal-virginia-haunted-history/'),

  ('elmwood-cemetery-norfolk', 'Elmwood Cemetery', 'Norfolk', '238 E Princess Anne Road', 'VA', 36.8562, -76.2665, 'cemetery', 'Established in 1853, this Victorian garden cemetery spans 55 acres and contains over 35,000 graves, including many Confederate soldiers and victims of yellow fever epidemics.', 'The ghost of a Confederate soldier stands eternal guard over his comrades'' graves. Visitors have photographed mysterious mists and orbs near the military section. A woman in mourning dress visits a particular grave each evening, weeping for her lost love before fading away.', 1853, 'https://www.hauntedplaces.org/norfolk-va/'),

  ('norfolk-naval-shipyard', 'Norfolk Naval Shipyard', 'Portsmouth', '1 Ave D', 'VA', 36.8205, -76.2936, 'other', 'The oldest and largest industrial facility belonging to the U.S. Navy, established in 1767. It has built, repaired, and modernized countless warships over its 250+ year history.', 'Workers report seeing the ghosts of sailors who died in shipyard accidents. The spirit of a worker killed during WWII appears in Dry Dock 4, still carrying his tools. Strange sounds of hammering and welding come from empty areas late at night.', 1767, 'https://www.hauntedplaces.org/norfolk-va/');

